import styled from "styled-components";

export const Container = styled.div`
    height: 732px;
    position: static;
    display: flex;
`;

export const ContainerL = styled.div`
    width: 50%;
    height: 100%;
    position: static;
    margin: auto;
    margin-top: 70px;
    display: flex;
    justify-content: center;
    background: linear-gradient(to bottom, #313e50, #EDC128);
`;

export const ContainerR = styled.div`
    width: 50%;
    height: 100%;
    position: static;
    margin: auto;
    margin-top: 70px;
    display: flex;
    justify-content: center;
    background: linear-gradient(to bottom, #313e50, #EDC128);
`;

export const Wrapper = styled.div`
    width: 100%;
    height: 95%;
