import styled from "styled-components";

export const Cabecera1 = styled.header`
    height: 9vh;
    width: 100vw;
    background-color: red;
`;

export const Cuerpo1 = styled.body`
    height: 90vh;
    width: 100vw;
    background-color: white;
    #col1{
        background-color: aliceblue;
    }
    #col2{
        background-color: yellowgreen;
    }
    .titulo{
        font-weight: bold;
        color: #EDC128;
    }
    .SubtituloPrincipal{
        text-align: center;
    }
`;

export const ImgPrincipal = styled.img`
    width: 40.6vw;
`;

export const VideoPrincipal = styled.iframe`
    width: 30vw;
    height: 40vh;
`;

export const ImgSec = styled.img`
    width: 20vw;
`;
